use serde::{Deserialize, Serialize};

use crate::entities::{Beacon, ProtocolParameters, Signer};

/// Structure to transport [crate::entities::CertificatePending] data.
#[derive(Clone, Debug, PartialEq, Default, Serialize, Deserialize)]
pub struct CertificatePendingMessage {
    /// Current Beacon
    pub beacon: Beacon,

    /// Current Protocol parameters
    #[serde(rename = "protocol")]
    pub protocol_parameters: ProtocolParameters,

    /// Next Protocol parameters
    #[serde(rename = "next_protocol")]
    pub next_protocol_parameters: ProtocolParameters,

    /// Current Signers
    pub signers: Vec<Signer>,

    /// Signers that will be able to sign on the next epoch
    pub next_signers: Vec<Signer>,
}

impl CertificatePendingMessage {
    /// Provide a dummy instance for test.
    pub fn dummy() -> Self {
        Self {
            beacon: Beacon::default(),
            protocol_parameters: ProtocolParameters {
                k: 5,
                m: 100,
                phi_f: 0.65,
            },
            next_protocol_parameters: ProtocolParameters {
                k: 50,
                m: 1000,
                phi_f: 0.65,
            },
            signers: [Signer::default()].to_vec(),
            next_signers: [Signer::default()].to_vec(),
        }
    }
}

#[cfg(test)]
mod tests {
    use crate::entities::Epoch;

    use super::*;

    fn golden_message() -> CertificatePendingMessage {
        CertificatePendingMessage {
            beacon: Beacon {
                network: "preview".to_string(),
                epoch: Epoch(86),
                immutable_file_number: 1728,
            },
            protocol_parameters: ProtocolParameters {
                k: 5,
                m: 100,
                phi_f: 0.65,
            },
            next_protocol_parameters: ProtocolParameters {
                k: 50,
                m: 1000,
                phi_f: 0.65,
            },
            signers: vec![Signer {party_id: "123".to_string(), verification_key:  "".to_string(), verification_key_signature: None, operational_certificate: None, kes_period: None }],
            next_signers: vec![Signer {party_id: "123".to_string(), verification_key:  "".to_string(), verification_key_signature: None, operational_certificate: None, kes_period: None }],
        }
    }

    #[test]
    fn test_v1() {
        let json = r#"{
"beacon": {"network": "preview", "epoch": 86, "immutable_file_number": 1728 },
"protocol": {"k": 5, "m": 100, "phi_f": 0.65 },
"next_protocol": {"k": 50, "m": 1000, "phi_f": 0.65 },
"signers": [{"party_id": "123", "verification_key":   ""}],
"next_signers": [{"party_id": "123", "verification_key":   ""}]
}"#;
        let message: CertificatePendingMessage = serde_json::from_str(json).unwrap();

        assert_eq!(golden_message(), message);
    }
}
