use serde::{Deserialize, Serialize};

use crate::{
    crypto_helper::KESPeriod,
    entities::{
        Epoch, HexEncodedOpCert, HexEncodedVerificationKey, HexEncodedVerificationKeySignature,
        PartyId,
    },
    test_utils::fake_keys,
};

era_deprecate!("make epoch of RegisterSignerMessage not optional");
/// Register Signer Message
#[derive(Clone, Debug, PartialEq, Eq, Serialize, Deserialize)]
pub struct RegisterSignerMessage {
    /// Epoch at which registration is sent
    /// #[serde(skip_serializing_if = "Option::is_none")]
    pub epoch: Option<Epoch>,

    /// The unique identifier of the signer
    // TODO: Should be removed once the signer certification is fully deployed
    pub party_id: PartyId,

    /// The public key used to authenticate signer signature
    pub verification_key: HexEncodedVerificationKey,

    /// The encoded signer 'Mithril verification key' signature (signed by the
    /// Cardano node KES secret key).
    // TODO: Option should be removed once the signer certification is fully
    //       deployed.
    #[serde(skip_serializing_if = "Option::is_none")]
    pub verification_key_signature: Option<HexEncodedVerificationKeySignature>,

    /// The encoded operational certificate of stake pool operator attached to
    /// the signer node.
    // TODO: Option should be removed once the signer certification is fully
    //       deployed.
    #[serde(skip_serializing_if = "Option::is_none")]
    pub operational_certificate: Option<HexEncodedOpCert>,

    /// The KES period used to compute the verification key signature
    // TODO: This KES period should not be used as is and should probably be
    //       within an allowed range of KES periods for the epoch.
    #[serde(skip_serializing_if = "Option::is_none")]
    pub kes_period: Option<KESPeriod>,
}

impl RegisterSignerMessage {
    /// Return a dummy test entity (test-only).
    pub fn dummy() -> Self {
        Self {
            epoch: Some(Epoch(1)),
            party_id: "pool1m8crhnqj5k2kyszf5j2scshupystyxc887zdfrpzh6ty6eun4fx".to_string(),
            verification_key: fake_keys::signer_verification_key()[0].to_string(),
            verification_key_signature: Some(fake_keys::signer_verification_key_signature()[0].to_string()),
            operational_certificate: Some("".to_string()),
            kes_period: Some(6),
        }
    }
}

#[cfg(test)]
mod tests {
    use super::*;

    // reference structure to compare with what should be deserialized.
    fn golden_message_v1() -> RegisterSignerMessage {
        RegisterSignerMessage {
            epoch: None,
            party_id: "pool1m8crhnqj5k2kyszf5j2scshupystyxc887zdfrpzh6ty6eun4fx".to_string(),
            verification_key: "".to_string(),
            verification_key_signature: Some("".to_string()),
            operational_certificate: Some("".to_string()),
            kes_period: Some(6),
        }
    }

    // reference structure to compare with what should be deserialized.
    fn golden_message_v2() -> RegisterSignerMessage {
        RegisterSignerMessage {
            epoch: Some(Epoch(123)),
            party_id: "pool1m8crhnqj5k2kyszf5j2scshupystyxc887zdfrpzh6ty6eun4fx".to_string(),
            verification_key: "".to_string(),
            verification_key_signature: Some("".to_string()),
            operational_certificate: Some("".to_string()),
            kes_period: Some(6),
        }
    }

    // Test the retro compatibility with possible future upgrades.
    #[test]
    fn test_v1() {
        let json = r#"{
"party_id": "pool1m8crhnqj5k2kyszf5j2scshupystyxc887zdfrpzh6ty6eun4fx",
"verification_key": "",
"verification_key_signature": "",
"operational_certificate": "",
"kes_period": 6
}"#;
        let message: RegisterSignerMessage = serde_json::from_str(json).expect(
            "This JSON is expected to be succesfully parsed into a RegisterSignerMessage instance.",
        );

        assert_eq!(golden_message_v1(), message);
    }

    // Test the retro compatibility with possible future upgrades.
    #[test]
    fn test_v2() {
        let json = r#"{
"epoch": 123,
"party_id": "pool1m8crhnqj5k2kyszf5j2scshupystyxc887zdfrpzh6ty6eun4fx",
"verification_key": "",
"verification_key_signature": "",
"operational_certificate": "",
"kes_period": 6
}"#;
        let message: RegisterSignerMessage = serde_json::from_str(json).expect(
            "This JSON is expected to be succesfully parsed into a RegisterSignerMessage instance.",
        );

        assert_eq!(golden_message_v2(), message);
    }
}
